import React, {useState, Component} from "react";
import './HomePage.css';
import FlashCard from './components/Flashcard.js'
import ParseCard from './components/Parsecard.js'


class HomePage extends Component {
    constructor(props) {
        super(props)
        this.state = {
            cardType: "FlashCard",
            newCardFront: "Write",
            newCardBack: "Write Back",
            newCardHint: "Notes/Hints",
            newCardDecks: "Deck"
        }
        this.toggleCardType = this.toggleCardType.bind(this);
    }


    updateHandler = (newFront, newBack, newHint, newDecks) => {
        console.log("updating!");
        let tempCard = {
            newCardFront: newFront,
            newCardBack: newBack,
            newCardHint: newHint,
            newCardDecks: newDecks 
        }
        this.setState({card: tempCard});
    }

    addCardHandler = () => {

    }

    toggleCardType = () => {
        if (this.state.cardType ==="FlashCard") {
            this.setState({cardType: "ParseCard"});
        }
        else {
            this.setState({cardType: "FlashCard"});
        }
        console.log("Toggling Card!");
    }

    render() {
        return (
            <div>
                {this.state.cardType === "FlashCard" && <FlashCard type={"Normal"} frontText={this.newCardFront} backText={this.newCardBack} cardHint={this.newCardHint} cardDecks={this.newCardDecks} updated = {this.updateHandler}></FlashCard>}
                {this.state.cardType === "ParseCard" && <ParseCard></ParseCard>}
                <div className="HomeCardButtons">
                    <label className="Switch">
                        <input type="checkbox"></input>
                        <span className = "slider round" onClick={this.toggleCardType}></span>
                    </label>
                    <button className="AddCardButton" onClick={this.addCardHandler}>&#43;</button>
                </div>
            </div>


        )
    }
}

export default HomePage;
