import React, {Component} from 'react';
import './Deckcard.css';
import styled from 'styled-components';

class DeckCard extends Component {
    constructor(props) {
        super(props)
        this.state = {
            localDeckName: this.props.deckName
        }
    }

    handleDeckNameChange = (event) => {
        let newDeckName = event.taget.value;
        this.setState({localDeckName: newDeckName});

    }

    render() {
        return (
            <div className = "DeckCard">
                <div className="DeckCardInner">
                    <div className="DeckDot"></div>
                    <div className="DeckName" data-testid="DeckName" onClick={this.props.study} value={this.props.deckName}>{this.props.deckName}</div>
                    <button className="DownloadButton" src={`./DownloadButton.png`} onClick={this.props.download}></button>
                </div>
            </div>
        )
    }
}




export default DeckCard
