import React, {Component} from 'react';
import './Flashcard.css';

class FlashCard extends Component {
    constructor(props) {
        super(props)
        this.state = {
            side: "front",
            type: this.props.type,
            localFrontText: this.props.frontText,
            localBackText: this.props.backText,
            localCardHint: this.props.cardHint,
            localCardDecks: this.props.cardDecks,
            flipState: false
        }
        this.flipCard = this.flipCard.bind(this);

    }

    flipCard = (event) => {
        console.log("Flipping from: ", this.state.side);
        this.setState({flipState: !this.state.flipState});
        if (this.state.side === "front") {
            this.setState({side: "back"});
        }
        else {
            this.setState({side: "front"});
        }
        console.log("flipState:", this.state.flipState);
    }

    render() {
        return (
            <div className="FlashCard">
                <div className="FlashCardInner" style={{transform: this.state.flipState ? "rotateX(180deg)": ""}}>
                    <div className="FlashCardFront">
                        <top>
                            <ul>
                                <li><span className="Dot"></span></li>
                                <li><input type="CardHint" value="Notes/Hints"></input></li>
                                <li><input type="CardDeck" className="CardDeck" value="Deck"></input></li>
                            </ul>
                        </top>
                        <input type="CardFrontText" value="Write"></input>
                        <button className="FlipCardButton" onClick={this.flipCard}></button>
                    </div>
                    <div className="FlashCardBack">
                        <top>
                            <ul>
                                <li><span className="Dot"></span></li>
                                <li><input type="CardHint" value="Notes/Hints"></input></li>
                                <li><input type="CardDeck" className="CardDeck" value="Deck"></input></li>
                            </ul>
                        </top>
                        <input type="CardFrontText" value="Write"></input>
                        <button className="FlipCardButton" onClick={this.flipCard}></button>
                    </div>
                </div>
            </div>
        );
    }

}
export default FlashCard
