import React, {Component} from 'react';
import './Colltab.css';

class CollTab extends Component{
    constructor(props) {
        super(props)
        this.state = {
            render: false
        }
        this.toggleContent = this.toggleContent.bind(this);
    }

    toggleContent() { //This currently works although it is quite ugly
        this.setState({render: !this.state.render});
    }

    toggleContentAnimate() { //This needs some work
        this.classList.toggle("active");
        var content = this.nextElementSibling;
        if (content.style.display === "block") {
            content.style.display = "none";
        } else {
            content.style.display = "block";
        }
    }

    render() {
    return (
        <div className="CollTab">
            <button onClick={this.toggleContent}>{this.props.frontText}</button>
            <div 
                className="content"
                style={{display: this.state.render ? 'block': 'none', transition: 'max-height 0.2s ease-out', maxHeight: '0', overflow: 'hidden', padding: '0 18px'}}>
                <p>Here is some stuff about me!</p>
            </div>
        </div>
    )
    }
};

export default CollTab;
