import React, {useState, Component} from "react";
import CollTab from './components/Colltab';

const cards =  [
    {id: 0, cardColor: "Red", frontText: "front One!", backText: "back One!", cardHint: "cardHint One!", cardDecks: "Apples..."},
    {id: 1, cardColor: "Red", frontText: "front TWo!", backText: "back TWo!", cardHint: "cardHint TWO!", cardDecks: "Apples..."},
    {id: 2, cardColor: "Red", frontText: "front THREE!", backText: "back Three", cardHint: "cardHint Three!", cardDecks: "Apples..."},
]





class CollectPage extends Component {

    deleteHandler = (id) => {
        console.log("Time to delete: ", id);
        for (let i = 0; i < cards.length; i++) {
            if (cards[i].id === id) {
                cards.remove(i);
            }
        }
    }

    updateHandler = (id) => {
        console.log("Time to update: ", id);
    }

    render() {
    return (
        <div>
            {cards.map((card, option) => (
                <CollTab key = {option} id = {card.id} frontText = {card.frontText} backText = {card.backText} cardHint = {card.cardHint} cardDecks = {card.cardDecks} deleted = {this.deleteHandler} updated = {this.updateHandler}></CollTab>
            ))}
        </div>
    )};
}

export default CollectPage;
