import React, {useState, Component} from "react";
import './QuizPage.css';
import FlashCard from './components/Flashcard.js'


class QuizPage extends Component {
    constructor(props) {
        super(props)
        this.state = {
            cardArray: this.props.cardArray,
            frontText: this.props.frontText,
            backText: this.props.backText,
            cardHint: this.props.cardHint,
            cardDecks: this.props.cardDecks,
            cardType: "QuizCard",
        }
    }



    nextCardHandler = (rightOrWrong) => {
        if (rightOrWrong === "Correct") {
            console.log("The user was correct!");
        }
        else {
            console.log("The user was Incorrect!");
        }
    }

    render() {
        return (
            <div>
                <FlashCard type={"Quiz"} frontText={this.state.currentCardFront} backText={this.state.currentCardBack} cardHint={this.state.currentCardHint} cardDecks={this.state.currentCardDecks}></FlashCard>
                <div className="QuizPageButtons">
                    <button className="CorrectButton" onClick={() => this.nextCardHandler("Correct")}>Correct</button>
                    <button className="IncorrectButton" onClick={() => this.nextCardHandler("Incorrect")}>Incorrect</button>
                </div>
            </div>
        )
    }
}

export default QuizPage;
