import React, {useState, Component} from "react";
import './DecksPage.css';
import DeckCard from "./components/Deckcard.js"
import styled from 'styled-components';


class DeckPage extends Component {
    constructor(props) {
        super(props)
        let tempDecksList = []
        for (let i = 0; i < this.props.cardArray.length; i++) {
            for (let j = 0; j < this.props.cardArray[i].cardDecks.length; j++) {
                if (!tempDecksList.includes(this.props.cardArray[i].cardDecks[j])) {
                    tempDecksList.push(this.props.cardArray[i].cardDecks[j])

                }
            }
        }
        console.log("Decks List: ", tempDecksList);
        this.state = {
            decksList: [...tempDecksList],
            deckOne: tempDecksList.length > 0 ? tempDecksList[0]: "Deck Name",
            deckTwo: tempDecksList.length > 1 ? tempDecksList[1]: "Deck Name",
            deckThree: tempDecksList.length > 2 ? tempDecksList[2]: "Deck Name",
            deckFour: tempDecksList.length > 3 ? tempDecksList[3]: "Deck Name",
            page: 0
        }
        console.log("deckOne:", this.state.deckOne);
    }

    nextPageHandler = () => {
        console.log("Time to go forward!");

    }

    prevPageHandler = () => {
        console.log("Time to go back!");
    }

    downloadHandler = (deckIndex) => {
        console.log("Trying to download from deckpage at index: ", deckIndex);
        if (deckIndex > this.state.decksList.length) {
            return; //DONT DO ANYTHING IF ITS OUT OF BOUNDS!
        }
        else {
            //HANDLE THE DOWNLOADING
        }
    }

    studyHandler = (deckIndex) => {
        console.log("Trying to study from deckpage at index: ", deckIndex);
        if (deckIndex > this.state.decksList.length) {
            return; //If it is out of bounds, we can actually just go to the homepage to start a new deck!
        }
        else {
            //HANDLE THE DOWNLOADING
        }
    }

    render() {
        return (
            <div className= "DecksContainer">
                <div className = "title">
                    <p>Decks</p>
                    <div className="PageButtons">
                            <button className="PageLeft" onClick={() => this.nextPageHandler()}>
                                <LeftArrow src={`https://file.rendit.io/n/diSt5xpFMowspip8kpb8.svg`}></LeftArrow>
                            </button>
                            <button className="PageRight" onClick={() => this.prevPageHandler()}>
                                <RightArrow src={`https://file.rendit.io/n/YpUpJC5E4gqrKbyJxvYn.svg`}></RightArrow>
                            </button>
                    </div>
                </div>
                <div className = "DecksWindow">
                    <row className = "DecksRow">
                        <DeckCard deckName={this.state.deckOne} download={() => this.downloadHandler(this.state.page + 0)} study={() => this.studyHandler(this.state.page + 0)}></DeckCard>
                        <DeckCard deckName={this.state.deckTwo} download={() => this.downloadHandler(this.state.page + 1)} study={() => this.studyHandler(this.state.page + 1)}></DeckCard>
                    </row>
                    <row className = "DecksRow">
                        <DeckCard deckName={this.state.deckThree} download={() => this.downloadHandler(this.state.page + 2)} study={() => this.studyHandler(this.state.page + 2)}></DeckCard>
                        <DeckCard deckName={this.state.deckFour} download={() => this.downloadHandler(this.state.page + 3)} study={() => this.studyHandler(this.state.page + 3)}></DeckCard>
                    </row>
                </div>
            </div>
        )
    }

}


const LeftArrow = styled.img`
  width: 15px;
  height: 20px;
`;

const RightArrow = styled.img`
  width: 15px;
  height: 20px;
`;

export default DeckPage;
