import React, {useState} from "react";
import './HomePage.css';
let flashCardSide = "front";

const HomePage = () => {
    function flipToFront() {
        var backDetails = document.getElementById("cardDetailsBackIn").value;
        var backDeck = document.getElementById("cardDeckBackIn").value;
        document.getElementById("cardDetailsFrontIn").value = backDetails;
        document.getElementById("cardDeckFrontIn").value = backDeck;
        flashCardSide = "front";
        flipCard();
    }

    function flipToBack() {
        var frontDetails = document.getElementById("cardDetailsFrontIn").value;
        var frontDeck = document.getElementById("cardDeckFrontIn").value;
        document.getElementById("cardDetailsBackIn").value = frontDetails;
        document.getElementById("cardDeckBackIn").value = frontDeck;
        flashCardSide = "back";
        flipCard();
    }

    function flipCard() {
        var card = document.getElementById("flashcard-inner");
        card.classList.toggle("flipCard");
    }

    return (
        <div className="flashcardCreatePage" id = "flashcardCreatePage">
            <div className="flashcardContainerClass" id="flashcardContainerId">
                <div className="flashcard" id="flashcard">
                    <div className="flashcard-inner" id="flashcard-inner">
                        <div className="flashcard-front">
                            <top>
                                <ul>
                                    <li><span className="dot"></span></li>
                                    <li><input type="cardClass" placeholder="Notes/Hints" id="cardDetailsFrontIn"></input></li>
                                    <a><input type="deckName" placeholder="Deck" class="cardDeck" id="cardDeckFrontIn"></input></a>
                                </ul>
                            </top>
                            <input type="cardFrontText" placeholder="Write" id="cardFrontTextIn"></input>
                            <button className = "flipCardButton" onClick="flipToBack();"></button>
                        </div>
                        <div className="flashcard-back">
                            <top>
                                <ul>
                                    <li><span className="dot"></span></li>
                                    <li><input type="cardClass" placeholder="Notes/Hints" id="cardDetailsBackIn"></input></li>
                                    <a><input type="deckName" placeholder="Deck" className="cardDeck" id="cardDeckBackIn"></input></a>
                                </ul>
                            </top>
                        <input type="cardBackText" placeholder="Write Back" id="cardBackTextIn"></input>
                        <button className = "flipCardButton" onClick="flipToFront();"></button>
                        </div>
                    </div>
                </div>
                <div className="flashcardTextInput" id="flashcardTextInput">
                    <div className="flashcardTextInputInner" id="flashcardTextInputInner">
                        <top>
                            <ul>
                                <li><span className="dot"></span></li>
                                <li><input type="cardClass" placeholder="Notes/Hints" id="cardDetailsParseMode"></input></li>
                                <a><input type="deckName" placeholder="Deck" className="cardDeck" id="cardDeckParseMode"></input></a>
                            </ul>
                        </top>
                        <textarea id="flashcardTextInputTextBox">Please Type Here:</textarea>
                    </div>
                </div>
                <div className="bottomButtonBar">
                    <ul>
                        <li><button className = "addCardButton" id = "addCardButton" onClick="addCard();">&#43;</button></li>
                        <li><label className = "switch" id="flashcardParseMode" onClick="toggleFlashcardMode()">
                            <input type = "checkbox" id = "flashcardParseModeSwitch"></input>
                            <span className = "slider round"></span>
                        </label></li>
                    </ul>
                </div>
            <div className="errorMessageHomeContainer" id="errorMessageHomeContainer">
                <p className="errorMessage" id="errorMessageHome">Please fill in all fields!</p>
            </div>

            </div>
        </div>
    );
};
export default HomePage;
