import React, {Component} from 'react';
import './Colltab.css';

class CollTab extends Component{    //Still need to figure out how to pass changes up from the tab!
    constructor(props) {
        super(props)
        this.state = {
            render: false,
            localFrontText: this.props.frontText,
            localBackText: this.props.backText,
            localCardHint: this.props.cardHint,
            localCardDecks: this.props.cardDecks
        }
        this.toggleContent = this.toggleContent.bind(this);
    }


    toggleContent() { //This currently works although it is quite ugly and doesn't do any animation
        this.setState({render: !this.state.render});
    }


    handleFrontChange = (event) => {
        this.setState({localFrontText: event.target.value});
    }

    handleBackChange = (event) => {
        this.setState({localBackText: event.target.value});
    }

    handleHintChange = (event) => {
        this.setState({localCardHint: event.target.value});
    }

    handleDecksChange = (event) => {
        this.setState({localCardDecks: event.target.value});
    }


    render() {
        const buttonStyle = {
            backgroundColor: 'white',
            font: 'inherit',
            border: '1px solid blue',
            padding: '8px',
            cursor: 'pointer'
        };
    return (
        <div className="CollTab">
            <button onClick={this.toggleContent}>{this.state.localFrontText}</button>
            <div 
                className="content"
                style={{display: this.state.render ? 'block': 'none'}}>
                <p>Here is some stuff about me!</p>
                <input type="text" onChange={this.handleFrontChange} value={this.state.localFrontText}></input>
                <input type="text" onChange={this.handleBackChange} value={this.state.localBackText}></input>
                <input type="text" onChange={this.handleHintChange} value={this.state.localCardHint}></input>
                <input type="text" onChange={this.handleDecksChange} value={this.state.localCardDecks}></input>
                <div className="CollTabButtons">
                    <button
                        style={buttonStyle}
                        onClick={() => this.updateAllHandler()}>Update Card</button>
                    <button
                        style={buttonStyle}
                        onClick={() => this.updateAllHandler()}>Delete Card</button>
                </div>
            </div>
        </div>
    )
    }
};

export default CollTab;
