import React, {Component} from 'react';
import './Flashcard.css';

class FlashCard extends Component {
    constructor(props) {
        super(props)
        this.state = {
            side: "front",
            type: this.props.type,
            localCardFront: this.props.frontText,
            localCardBack: this.props.backText,
            localCardDecks: this.props.cardDecks,
            localCardHint: this.props.cardHint,
            flipState: false
        }
        this.flipCard = this.flipCard.bind(this);

    }

    flipCard = (event) => {
        console.log("Flipping from: ", this.state.side);
        this.setState({flipState: !this.state.flipState});
        if (this.state.side === "front") {
            this.setState({side: "back"});
        }
        else {
            this.setState({side: "front"});
        }
        console.log("flipState:", this.state.flipState);
        console.log(this.state.localCardFront);
    }

    handleFrontChange = (event) => {
        console.log("Changing the front!");
        this.setState({localCardFront: event.target.value});
        console.log("NewState for front: ", this.state.localCardFront);
    }

    handleBackChange = (event) => {
        this.setState({localCardBack: event.target.value});
    }

    handleHintChange = (event) => {
        this.setState({localCardHint: event.target.value});
    }

    handleDecksChange = (event) => {
        this.setState({localCardDecks: event.target.value});
    }


    render() {
        return (
            <div className="FlashCard">
                <div className="FlashCardInner" style={{transform: this.state.flipState ? "rotateX(180deg)": ""}}>
                    <div className="FlashCardFront">
                        <top>
                            <ul>
                                <li><span className="Dot"></span></li>
                                <li><input type="CardHint" onChange={this.handleHintChange} value={this.state.localCardHint}></input></li>
                                <li><input type="CardDeck" onChange={this.handleDecksChange} className="CardDeck" value={this.state.localCardDecks}></input></li>
                            </ul>
                        </top>
                        <input type="CardFrontText" onChange = {this.handleFrontChange} value={this.state.localCardFront}></input>
                        <button className="FlipCardButton" onClick={this.flipCard}></button>
                    </div>
                    <div className="FlashCardBack">
                        <top>
                            <ul>
                                <li><span className="Dot"></span></li>
                                <li><input type="CardHint" onChange={this.handleHintChange} value={this.state.localCardHint}></input></li>
                                <li><input type="CardDeck" onChange={this.handleDecksChange} className="CardDeck" value={this.state.localCardDecks}></input></li>
                            </ul>
                        </top>
                        <input type="CardFrontText" onChange = {this.handleBackChange} value={this.state.localCardBack}></input>
                        <button className="FlipCardButton" onClick={this.flipCard}></button>
                    </div>
                </div>
            </div>
        );
    }

}
export default FlashCard
