import React, {Component} from 'react';
import './Colltab.css';

class CollTab extends Component{
    constructor(props) {
        super(props)
        this.state = {
            render: false
        }
        this.toggleContent = this.toggleContent.bind(this);
    }

    toggleContent() { //This currently works although it is quite ugly and doesn't do any animation
        this.setState({render: !this.state.render});
    }


    updateAllHandler = (newFrontText, newBackText, newCardHint, newCardDecks) => {
        this.setState({frontText: newFrontText});
        this.props.backText = newBackText;
        this.props.cardHint = newCardHint;
        this.props.newCardDecks = newCardDecks;
    }

    render() {
        const buttonStyle = {
            backgroundColor: 'white',
            font: 'inherit',
            border: '1px solid blue',
            padding: '8px',
            cursor: 'pointer'
        };
    return (
        <div className="CollTab">
            <button onClick={this.toggleContent}>{this.props.frontText}</button>
            <div 
                className="content"
                style={{display: this.state.render ? 'block': 'none'}}>
                <p>Here is some stuff about me!</p>
                <input type="text" defaultValue={this.props.frontText}></input>
                <input type="text" defaultValue={this.props.backText}></input>
                <input type="text" defaultValue={this.props.cardHint}></input>
                <input type="text" defaultValue={this.props.cardDecks}></input>
                <div className="CollTabButtons">
                    <button
                        style={buttonStyle}
                        onClick={() => this.updateAllHandler()}>Update Card</button>
                </div>
            </div>
        </div>
    )
    }
};

export default CollTab;
