import React, {useState, Component} from "react";
import './DecksPage.css';
import DeckCard from "./components/Deckcard.js"
import styled from 'styled-components';
import { isCompositeComponent } from "react-dom/test-utils";


class DeckPage extends Component {
    constructor(props) {
        super(props)
        this.state = {
            decksList: this.props.decksList,
            deckOne: this.props.decksList.length > 0 ? this.props.decksList[0]: "Deck Name",
            deckTwo: this.props.decksList.length > 1 ? this.props.decksList[1]: "Deck Name",
            deckThree: this.props.decksList.length > 2 ? this.props.decksList[2]: "Deck Name",
            deckFour: this.props.decksList.length > 3 ? this.props.decksList[3]: "Deck Name",
            page: 0,
            numDecks: this.props.decksList.length
        }
        this.nextPageHandler = this.nextPageHandler.bind(this);
    }


    nextPageHandler = (what) => {
        console.log("Time to go forward from page: ", this.state.page);
        console.log("Contents of desksList", this.state.decksList);
        console.log("Current deckOne", this.state.deckOne);
        if (((this.state.page * 4) + 5) < this.props.decksList.length) {
            //We can go to the next page
            console.log("Trying to go forward to the next page!");
            let newDeckOne = this.props.decksList.length > ((this.state.page + 1) * 4 + 0) ? this.props.decksList[(this.state.page + 1) * 4 + 0] : "Deck Name";
            let newDeckTwo = this.props.decksList.length > ((this.state.page + 1) * 4 + 1) ? this.props.decksList[(this.state.page + 1) * 4 + 1] : "Deck Name";
            let newDeckThree = this.props.decksList.length > ((this.state.page + 1) * 4 + 2) ? this.props.decksList[(this.state.page + 1) * 4 + 2] : "Deck Name";
            let newDeckFour = this.props.decksList.length > ((this.state.page + 1) * 4 + 3) ? this.props.decksList[(this.state.page + 1) * 4 + 3] : "Deck Name";
            console.log("newDeckOne: ", newDeckOne);
            console.log("newDeckTwo: ", newDeckTwo);
            console.log("newDeckThree: ", newDeckThree);
            console.log("newDeckFour: ", newDeckFour);
            let newPage = this.state.page + 1;
            console.log("newPage should be: ", newPage);
            //I HAVE NO IDEA WHY THIS WORKS AND REGULAR SETSTATE DOES NOT WORK
            this.state = (this.setState = ({
                decksList: this.props.decksList,
                deckOne: newDeckOne,
                deckTwo: newDeckTwo,
                deckThree: newDeckThree,
                deckFour: newDeckFour,
                page: newPage,
                numDecks: this.props.decksList.length
            }));
        }
        else {
            console.log("We cannot go any further");
        }
    }

    prevPageHandler = () => {
        console.log("Time to go back from page: ", this.state.page);
    }

    downloadHandler = (deckIndex) => {
        console.log("Trying to download from deckpage at index: ", deckIndex, "onPage: ", this.state.page);
        if (deckIndex > this.state.decksList.length) {
            return; //DONT DO ANYTHING IF ITS OUT OF BOUNDS!
        }
        else {
            //HANDLE THE DOWNLOADING
        }
    }

    studyHandler = (deckIndex) => {
        console.log("Trying to study from deckpage at index: ", deckIndex, "onPage: ", this.state.page);
        if (deckIndex > this.state.decksList.length) {
            return; //If it is out of bounds, we can actually just go to the homepage to start a new deck!
        }
        else {
            //HANDLE THE DOWNLOADING
        }
    }

    render() {
        return (
            <div className= "DecksContainer">
                {this.state.numDecks >= 0 && <div className="ContentContainer">
                <div className="title">
                    <p>Decks</p>
                    <div className="PageButtons">
                            <button className="PageLeft" data-testid="PrevPageButton" onClick={() => this.prevPageHandler("prev")}>
                                <LeftArrow src={`https://file.rendit.io/n/diSt5xpFMowspip8kpb8.svg`}></LeftArrow>
                            </button>
                            <button className="PageRight" data-testid="NextPageButton" onClick={() => this.nextPageHandler("next")}>
                                <RightArrow src={`https://file.rendit.io/n/YpUpJC5E4gqrKbyJxvYn.svg`}></RightArrow>
                            </button>
                    </div>
                </div>
                <div className = "DecksWindow">
                    <row className = "DecksRow">
                        <DeckCard deckName={this.state.deckOne} download={() => this.downloadHandler(this.state.page + 0)} study={() => this.studyHandler(this.state.page + 0)}></DeckCard>
                        <DeckCard deckName={this.state.deckTwo} download={() => this.downloadHandler(this.state.page + 1)} study={() => this.studyHandler(this.state.page + 1)}></DeckCard>
                    </row>
                    <row className = "DecksRow">
                        <DeckCard deckName={this.state.deckThree} download={() => this.downloadHandler(this.state.page + 2)} study={() => this.studyHandler(this.state.page + 2)}></DeckCard>
                        <DeckCard deckName={this.state.deckFour} download={() => this.downloadHandler(this.state.page + 3)} study={() => this.studyHandler(this.state.page + 3)}></DeckCard>
                    </row>
                </div>
                </div> }
            </div>
        )
    }

}


const LeftArrow = styled.img`
  width: 15px;
  height: 20px;
`;

const RightArrow = styled.img`
  width: 15px;
  height: 20px;
`;

export default DeckPage;
