import React, {Component} from 'react';
import './Colltab.css';

class CollTab extends Component{
    constructor(props) {
        super(props)
        this.state = {
            render: false
        }
        this.toggleContent = this.toggleContent.bind(this);
    }

    toggleContent() { //This currently works although it is quite ugly
        this.setState({render: !this.state.render});
    }

    toggleContentAnimate() { //This needs some work
        if (this.content.style.display === "block") {
            this.content.style.display = "none";
        } else {
            this.content.style.display = "block";
        }
    }

    render() {
    return (
        <div className="CollTab">
            <button onClick={this.toggleContentAnimate}>{this.props.frontText}</button>
            <div 
                className="content">
                <p>Here is some stuff about me!</p>
            </div>
        </div>
    )
    }
};

export default CollTab;
