import React, {useState, Component} from "react";
import CollTab from './components/Colltab';


class CollectPage extends Component {

    constructor(props) {
        super(props)
        this.state = {
            cardArray: this.props.cardArray
        }
    }




    deleteHandler = (id) => {
        console.log("Time to delete: ", id);
        this.setState((cardArray) => 
            cardArray.filter((card) => card.id !== id)
        );
        console.log(this.state.cardArray);
    }

    updateHandler = (id) => {
        console.log("Time to update: ", id);
    }

    render() {
    return (
        <div>
            {this.state.cardArray.map((card, key) => (
                <CollTab key={key} id = {card.id} frontText = {card.frontText} backText = {card.backText} cardHint = {card.cardHint} cardDecks = {card.cardDecks} deleted = {this.deleteHandler} updated = {this.updateHandler}></CollTab>
            ))}
        </div>
    )};
}

export default CollectPage;
