import React, {useState, Component} from "react";
import CollTab from './components/Colltab';


class CollectPage extends Component {

    constructor(props) {
        super(props)
        this.state = {
            cardArray: this.props.cardArray
        }
    }




    deleteHandler = (id) => {
        console.log("Time to delete: ", id);
        this.setState({cardArray: this.state.cardArray.filter((card) => card.id !== id)}
        );
        console.log(this.state.cardArray);
    }

    updateHandler = (id, newFront, newBack, newHint, newDecks) => {
        let tempCardArray = [...this.state.cardArray];
        let cardIndex = 0;
        for (let i = 0; i < tempCardArray.length; i++) {
            if (tempCardArray[i].id === id) {
                cardIndex = i;
                break;
            }
        }

        let cardToChange = {...tempCardArray[cardIndex]};
        cardToChange.frontText = newFront;
        cardToChange.backText = newBack;
        cardToChange.cardHint = newHint;
        cardToChange.cardDecks = newDecks;

        console.log("Time to update: ", id);
    }

    render() {
    return (
        <div>
            {this.state.cardArray.map((card) => (
                <CollTab key={card.id} id = {card.id} frontText = {card.frontText} backText = {card.backText} cardHint = {card.cardHint} cardDecks = {card.cardDecks} deleted = {this.deleteHandler} updated = {this.updateHandler}></CollTab>
            ))}
        </div>
    )};
}

export default CollectPage;
